module.exports.createJob = function(expire, callback){
    var handler = null;
    var timer = function(){

    if(callback != null) callback(); // on finish
        clearTimeout(handler);
    }
    handler = setTimeout(timer, expire);
    return handler;
}

module.exports.deleteJob = function(jobHandler){
    clearTimeout(jobHandler);
}